import { FilePond, registerPlugin } from 'react-filepond';;
import React from 'react';
import FilePondPluginImageExifOrientation from 'filepond-plugin-image-exif-orientation';
import FilePondPluginImagePreview from 'filepond-plugin-image-preview';
/*
    We need to register the required plugins to do image manipulation and previewing.
    */
    registerPlugin(
    	FilePondPluginImageExifOrientation, FilePondPluginImagePreview
    );

export class Upload extends React.Component {
constructor(props) {
        super(props)
        this.state = {
            file: null,
        }
      }
changeFile = (e) => {
              this.setState({file : e.target.value});
}

render(){

    return(
    <>
        {/* Pass FilePond properties as attributes */}
                <FilePond ref={ref => this.pond = ref}
                          allowMultiple={false}
                          onupdatefiles={this.changeFile}>
                </FilePond>
        <button onClick={this.props.importFile}>Import</button>
         </>);
};
}
