import { FilePond, registerPlugin, FilePondPluginFileEncode, FilePondPluginFileValidateType, FilePondPluginImageExifOrientation,
  FilePondPluginImagePreview, FilePondPluginImageCrop, FilePondPluginImageResize, FilePondPluginImageTransform} from 'react-filepond';
import React from 'react';

export class Upload extends React.Component {
constructor(props) {
        super(props)
        this.state = {
            file: null,
        }
      }
changeFile = (e) => {
              this.setState({file : e.target.value});
}

render(){
    /*
    We need to register the required plugins to do image manipulation and previewing.
    */
    registerPlugin(
    	// encodes the file as base64 data
      FilePondPluginFileEncode,

    	// validates files based on input type
      FilePondPluginFileValidateType,

    	// corrects mobile image orientation
      FilePondPluginImageExifOrientation,

    	// previews the image
      FilePondPluginImagePreview,

    	// crops the image to a certain aspect ratio
      FilePondPluginImageCrop,

    	// resizes the image to fit a certain size
      FilePondPluginImageResize,

    	// applies crop and resize information on the client
      FilePondPluginImageTransform
    );

    return(
    <>
        {/* Pass FilePond properties as attributes */}
                <FilePond ref={ref => this.pond = ref}
                          allowMultiple={false}
                          onupdatefiles={this.changeFile}>
                </FilePond>
        <button onClick={this.props.importFile}>Import</button>
         </>);
};
}
