import React from 'react';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import { API_ROOT } from '../constants';
import $ from 'jquery';
import { message } from 'antd';
import PickyDateTime from 'react-picky-date-time';


var tempTime = ["9:00", "9:30", "10:00", "10:30"]

const style = {
    height: '200px',
    width: '200px',
    margin: '2rem'
};

const button_style = {
    margin: '0.5rem'
};


export class Reserve extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
        id: this.props.resId,
        resInfo: {},
        showPickyDateTime: true,
        date: '30',
        month: '01',
        year: '2000',
        hour: '03',
        minute: '10',
        second: '40',
        meridiem: 'PM'
    }
    this.handleReserve = this.handleReserve.bind(this);
  }


  componentDidMount() {
    $.ajax({
            url: `${API_ROOT}/searchRes?Id=${this.state.id}`,
            method: 'GET',
            }).then((response) => {
                this.setState({
                    resInfo : JSON.parse(response)
                });
                  console.log(JSON.parse(response));
            },(error) => {
                    message.error(error.responseText);
            }).catch((error) => {
                    console.log(error);
            });
  }

  getTimeSlot() {
      //获取timeslot，database里存了这个吗？如果有用id把它调出来，我这里用了tempTime测试
      return tempTime;
  }

  handleReserve(e) {
      // 用ajax检查capacity然后reserve餐馆，我直接写了alert测试用

    e.preventDefault();
    alert("sucess")
  }

  render() {
      const {
        showPickyDateTime,
        date,
        month,
        year,
        hour,
        minute,
        second,
        meridiem
      } = this.state;
      const resInfo = this.state.resInfo
      const slots = this.getTimeSlot()
    return (
        <Container>
            <Row>
                <Col>
                {/* Col 1 for the Name and Picture of the Restaurant<br /> */}
                    <h1>{resInfo.restaurant}</h1>
                    <img src="https://i.loli.net/2019/04/15/5cb3e0210edb5.jpg" style={style} />
                </Col>
                <Col>
                    <h4>{resInfo.address}
                        <br/>contact: {resInfo.phone}</h4>
                    {slots.map((slot, index)=>{
                        return <Button key={index} variant="primary" style={button_style} onClick={this.handleReserve}>{slot}</Button>
                    })}
                {/* Col 2 for the Description and time slots of the Restaurant */}
                </Col>
                <Col>
                    // console.log(resInfo)
                        <PickyDateTime
                          size="m"
                          mode={1}
                          show={showPickyDateTime}
                          locale="en-us"
                          defaultTime={`${hour}:${minute}:${second} ${meridiem}`} // "HH:MM:SS AM"
                          defaultDate={month/date/year} // "MM/DD/YYYY"
                          onClose={() => this.setState({ showPickyDateTime: false })}
                          onYearPicked={res => this.onYearPicked(res)}
                          onMonthPicked={res => this.onMonthPicked(res)}
                          onDatePicked={res => this.onDatePicked(res)}
                          onResetDate={res => this.onResetDate(res)}
                          onResetDefaultDate={res => this.onResetDefaultDate(res)}
                          onSecondChange={res => this.onSecondChange(res)}
                          onMinuteChange={res => this.onMinuteChange(res)}
                          onHourChange={res => this.onHourChange(res)}
                          onMeridiemChange={res => this.onMeridiemChange(res)}
                          onResetTime={res => this.onResetTime(res)}
                          onResetDefaultTime={res => this.onResetDefaultTime(res)}
                          onClearTime={res => this.onClearTime(res)}
                        />
                </Col>
            </Row>
        </Container>
    );
  }
}
