import React from 'react';
import { Register } from './Register';
import { Login } from './Login';
import { Switch, Route, Redirect } from 'react-router-dom';
import { Home } from './Home';
import { Reserve } from './Reserve';

export class Main extends React.Component {
getLogin = () => {
   return this.props.isLoggedIn ? <Redirect to="/reserve/:resId"/> : <Login handleLogin={this.props.handleLogin}/>;
}

// 我没有后端和数据库，这里把你的改了，要改回来
getHome = () => {
    return <Home/>;
}

getReserve = ({match}) => {
  return this.props.isLoggedIn ? <Reserve resId={match.params.resId} /> : <Login/>;
}

getRoot = () => {
    return <Redirect to="/login"/>;
}

 render() {
   return (
     <div className="main">
        <Switch>
            <Route exact path="/" render={this.getRoot}/>
            <Route path="/register" component={Register}/>
            <Route path="/login" render={this.getLogin}/>
            <Route path="/home" render={this.getHome}/>
            <Route path="/reserve/:resId" component={this.getReserve} />
            <Route render={this.getRoot}/>
        </Switch>
     </div>
   );
 }
}
