import React from 'react';
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import { API_ROOT } from '../constants';
import { message, Form ,Input, Icon} from 'antd';
import $ from 'jquery';

export class ReservationCard extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            id : this.props.resvDetail.restaurant_Id,
            userId: this.props.userId,
            resDetail: {},
            userDetail: {}
        }
      }


    componentDidMount() {
 $.ajax({
        async: false,
        url: `${API_ROOT}/searchRes?Id=${this.state.id}`,
        method: 'GET',
        }).then((response) => {
                             this.setState({
                                 resDetail : JSON.parse(response)
                             });
                             console.log(JSON.parse(response));
                         },(error) => {
                             message.error(error.responseText);
                         }).catch((error) => {
                             console.log(error);
                         });
                     $.ajax({
                         url: `${API_ROOT}/getClientInfo?user_ID=${this.state.userId}`,
                         method: 'GET',
                         }).then((response) => {
                             this.setState({
                                 resDetail : JSON.parse(response)
                             });
                             console.log(JSON.parse(response));
                         },(error) => {
                             message.error(error.responseText);
                         }).catch((error) => {
                             console.log(error);
                         });
    }

    render() {
        return (
            <Card style={{ width: '18rem', margin: '1rem'}}>
            <Card.Img variant="top" src="https://i.loli.net/2019/04/15/5cb3e0210edb5.jpg" />
            <Card.Body>
            <Card.Title>{this.state.resDetail.restaurant}</Card.Title>
            <Card.Text>
                {this.state.resDetail.address}
                <br/>contact: {this.state.resDetail.phone}
            </Card.Text>

            </Card.Body>
        </Card>
        );
    }
}
