import React from 'react';
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'

export class ReservationCard extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            id : this.props.resDetail.id,
            userId: this.props.userId,
            resDetail: {},
            userDetail: {}
        }
      }


    componentDidMount() {
        $.ajax({
            url: `${API_ROOT}/searchRes?Id=${this.state.id}`,
            method: 'GET',
            }).then((response) => {
                this.setState({
                    resDetail : JSON.parse(response)
                });
                console.log(JSON.parse(response));
            },(error) => {
                message.error(error.responseText);
            }).catch((error) => {
                console.log(error);
            });
        $.ajax({
            url: `${API_ROOT}/getClientInfo?user_ID=${this.state.id}`,
            method: 'GET',
            }).then((response) => {
                this.setState({
                    resDetail : JSON.parse(response)
                });
                console.log(JSON.parse(response));
            },(error) => {
                message.error(error.responseText);
            }).catch((error) => {
                console.log(error);
            });

    }

    getReserveLink(id, userId) {
        // generate link by id
        const link = "/reserve/" + id + "&" + userId
        return link
    }

    render() {
        const link = this.getReserveLink(this.state.id, this.state.userId)
        return (
            <Card style={{ width: '18rem', margin: '1rem'}}>
            <Card.Img variant="top" src="https://i.loli.net/2019/04/15/5cb3e0210edb5.jpg" />
            <Card.Body>
            <Card.Title>{this.props.resvDetail.reservation_Id}</Card.Title>
            <Card.Text>
                {this.props.resDetail.address}
                <br/>contact: {this.props.resDetail.phone}
            </Card.Text>
            <Button variant="primary" href={link}>Reserve</Button>
            </Card.Body>
        </Card>
        );
    }
}
